import 'dart:convert';
import 'dart:typed_data';

import 'dart:ui';

import 'package:umbra_flutter/umbra_flutter.dart';

/// {@template giphy}
/// A Dart Shader class for the `giphy` shader.
/// {@endtemplate}
class Giphy extends UmbraShader {
  Giphy._() : super(_cachedProgram!);

  /// {@macro giphy}
  static Future<Giphy> compile() async {
    try {
      // Caching the program on the first compile call.
      _cachedProgram ??= await FragmentProgram.compile(
        spirv: Uint8List.fromList(base64Decode(_spirv)).buffer,
      );

      return Giphy._();
    } on Exception catch (err, stackTrace) {
      throw UmbraException(err, stackTrace);
    }
  }

  static FragmentProgram? _cachedProgram;

  Shader shader({
    required Size resolution,
    required double uTime,
  }) {
    return program.shader(
      floatUniforms: Float32List.fromList([
        uTime,
        resolution.width,
        resolution.height,
      ]),
      samplerUniforms: [],
    );
  }
}

const _spirv =
    '';
